import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ login }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.email || !formData.password) {
      setError('Please enter both email and password');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      console.log('Attempting login with:', formData.email);
      
      // Test backend connection first
      try {
        const testResponse = await axios.get('http://localhost:5000');
        console.log('Backend connection test:', testResponse.status);
      } catch (testError) {
        console.error('Backend connection failed:', testError);
        setError('Cannot connect to backend server. Make sure it\'s running on port 5000.');
        setLoading(false);
        return;
      }

      // Attempt login
      const result = await login(formData.email, formData.password);
      
      if (!result.success) {
        setError(result.message || 'Login failed. Check console for details.');
      }
    } catch (err) {
      console.error('Login error details:', err);
      setError(`Login failed: ${err.message}. Check browser console (F12) for details.`);
    }
    
    setLoading(false);
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h1>Welcome Back</h1>
        <p>Sign in to your inventory account</p>
        
        {error && (
          <div style={{
            color: 'red', 
            background: '#ffe6e6', 
            padding: '10px', 
            borderRadius: '5px',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            {error}
          </div>
        )}
        
        <div className="form-group">
          <label>Email Address</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="you@example.com"
            required
          />
        </div>
        
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="••••••••"
            required
          />
        </div>
        
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Signing In...' : 'Sign In'}
        </button>
        
        <div className="demo-note">
          <strong>Troubleshooting:</strong><br />
          1. Backend must be running on port 5000<br />
          2. Check browser console (F12) for errors<br />
          3. Try refreshing the page<br />
          4. Use any email/password combination
        </div>
      </form>
    </div>
  );
};

export default Login;
