import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalItems: 0,
    lowStockItems: 0,
    totalSales: 0,
    totalRevenue: 0
  });
  const [lowStockProducts, setLowStockProducts] = useState([]);
  const [recentSales, setRecentSales] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [productsRes, lowStockRes, salesRes] = await Promise.all([
        axios.get('/products'),
        axios.get('/products/low-stock'),
        axios.get('/sales')
      ]);

      const products = productsRes.data;
      const lowStock = lowStockRes.data;
      const sales = salesRes.data;

      setStats({
        totalItems: products.length,
        lowStockItems: lowStock.length,
        totalSales: sales.length,
        totalRevenue: sales.reduce((total, sale) => total + sale.totalAmount, 0)
      });

      setLowStockProducts(lowStock);
      setRecentSales(sales.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      
      <div className="stats-grid">
        <div className="stat-card total-items">
          <h3>Total Items</h3>
          <div className="number">{stats.totalItems}</div>
        </div>
        
        <div className="stat-card low-stock">
          <h3>Low Stock Items</h3>
          <div className="number">{stats.lowStockItems}</div>
        </div>
        
        <div className="stat-card total-sales">
          <h3>Total Sales</h3>
          <div className="number">{stats.totalSales}</div>
        </div>
        
        <div className="stat-card revenue">
          <h3>Revenue</h3>
            <div className="number">â‚¹{stats.totalRevenue.toFixed(2)}</div>        </div>
      </div>

      <div className="alert-section">
        <h2>Low Stock Alerts</h2>
        {lowStockProducts.length === 0 ? (
          <p>No low stock alerts</p>
        ) : (
          lowStockProducts.map(product => (
            <div key={product._id} className="alert-item">
              <strong>{product.name}</strong> - SKU: {product.sku} 
              <span style={{marginLeft: '10px', color: '#e74c3c'}}>
                (Only {product.quantity} left)
              </span>
            </div>
          ))
        )}
      </div>

      <div className="recent-sales">
        <h2>Recent Sales</h2>
        {recentSales.length === 0 ? (
          <p>No recent sales</p>
        ) : (
          recentSales.map(sale => (
            <div key={sale._id} style={{padding: '10px', borderBottom: '1px solid #eee'}}>
              <div>Total: ${sale.totalAmount.toFixed(2)}</div>
              <small>{new Date(sale.createdAt).toLocaleDateString()}</small>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Dashboard;
